// src/App.js
import React, { useState, useEffect } from 'react';
import FolderList from './components/FolderList';
import FolderDetails from './components/FolderDetails';
import './App.css';

const App = () => {
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Ganti dengan URL API Laravel Anda untuk mendapatkan daftar folder
    fetch('http://127.0.0.1:8000/api/folder')
      .then(response => response.json())
      .then(data => {
        if (data.status === 'success') {
          setFolders([data.data]); // Simpan data folder tunggal sebagai array
        } else {
          setError(data.message);
        }
      })
      .catch(error => setError('Error fetching data: ' + error))
      .finally(() => setLoading(false));
  }, []);

  const handleFolderSelect = (folder) => {
    // Ganti dengan URL API Laravel untuk mendapatkan subfolder
    fetch(`http://127.0.0.1:8000/api/${folder.id}/sub_folder`)
      .then(response => response.json())
      .then(data => {
        if (data.status === 'success') {
          // Tambahkan subfolder ke data folder yang dipilih
          setSelectedFolder({
            ...folder,
            sub_folders: data.data.sub_folders || []
          });
        } else {
          setError(data.message);
        }
      })
      .catch(error => setError('Error fetching subfolder data: ' + error));
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="app">
      <div className="left-panel">
        <FolderList folders={folders} onSelect={handleFolderSelect} />
      </div>
      <div className="right-panel">
        <FolderDetails folder={selectedFolder} />
      </div>
    </div>
  );
};

export default App;
