// src/components/FolderDetails.js
import React, { useState, useEffect } from 'react';

const FolderDetails = ({ folder }) => {
  const [subFolders, setSubFolders] = useState([]);

  useEffect(() => {
    if (folder) {
      fetch(`http://127.0.0.1:8000/api/${folder.id}/sub_folder`)
        .then(response => response.json())
        .then(data => {
          if (data.status === 'success') {
            setSubFolders(data.data.sub_folders || []);
          } else {
            console.error('Error fetching subfolder data:', data.message);
          }
        })
        .catch(error => console.error('Error fetching subfolder data:', error));
    }
  }, [folder]);

  return (
    <div>
      <h2>Sub Folder</h2>
      {folder ? (
        <div>
          <ul>
            {subFolders.length > 0 ? (
              subFolders.map(subfolder => (
                <li key={subfolder.id}>{subfolder.name}</li>
              ))
            ) : (
              <li>No subfolder</li>
            )}
          </ul>
        </div>
      ) : (
        <p></p>
      )}
    </div>
  );
};

export default FolderDetails;
