// src/components/FolderDetails.js
import React, { useState } from 'react';

const FolderDetails = ({ folder }) => {
  const [newFolderName, setNewFolderName] = useState('');

  const handleAddSubfolder = () => {
    console.log('Add subfolder');
    // Implementasi tambah subfolder
    // Misalnya, tampilkan prompt untuk nama subfolder baru
    const name = prompt("Enter the name of the new subfolder:");
    if (name) {
      // Tambahkan logika untuk mengirimkan permintaan POST ke API untuk menambah subfolder
      console.log("Adding subfolder with name:", name);
    }
  };

  const handleRenameFolder = () => {
    console.log('Rename folder');
    // Implementasi rename folder
    // Misalnya, tampilkan prompt untuk nama folder baru
    const newName = prompt("Enter the new name for the folder:");
    if (newName) {
      // Tambahkan logika untuk mengirimkan permintaan PATCH ke API untuk mengubah nama folder
      console.log("Renaming folder to:", newName);
    }
  };

  const handleDeleteFolder = () => {
    console.log('Delete folder');
    // Implementasi delete folder
    if (window.confirm("Are you sure you want to delete this folder?")) {
      // Tambahkan logika untuk mengirimkan permintaan DELETE ke API untuk menghapus folder
      console.log("Deleting folder with id:", folder.id);
    }
  };

  return (
    <div>
      <h2>Folder Details</h2>
      {folder ? (
        <div>
          <h3>{folder.name}</h3>
          <h4>Subfolders:</h4>
          <ul>
            {folder.sub_folders && folder.sub_folders.length > 0 ? (
              folder.sub_folders.map(subfolder => (
                <li key={subfolder.id}>{subfolder.name}</li>
              ))
            ) : (
              <li>No subfolders</li>
            )}
          </ul>
          <button onClick={handleAddSubfolder}>Add Subfolder</button>
          <button onClick={handleRenameFolder}>Rename Folder</button>
          <button onClick={handleDeleteFolder}>Delete Folder</button>
        </div>
      ) : (
        <p>Select a folder to view details</p>
      )}
    </div>
  );
};

export default FolderDetails;
